mapper.select:
SELECT 

FROM zcourse_sort a

WHERE a.id = #{id}

SELECT 

FROM zcourse_sort a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.parent_id = #{parentId}				 




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zcourse_sort a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zcourse_sort SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE zcourse_sort SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE parent_id = #{parentId}
