mapper.select:
SELECT 

FROM zcourse_user a

WHERE a.id = #{id}

SELECT 

FROM zcourse_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.courseid = #{courseid}


		AND a.userid = #{userid}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zcourse_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zcourse_user SET 	
	coursetime = #{coursetime},
	usertime = usertime+5,	
	lasttime = #{lasttime},		
	update_date = #{updateDate}			
WHERE id = #{id}

