mapper.select:
SELECT 

FROM zcourse_hour a

WHERE a.id = #{id}

SELECT
FROM zcourse_hour a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM zcourse_hour a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zcourse_hour SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
