mapper.select:
SELECT 

FROM zquestion a

WHERE a.id = #{id}

SELECT 

FROM zquestion a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zquestion a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zquestion a


	a.del_flag = #{DEL_FLAG_NORMAL} 

		AND a.type = #{type}



		AND a.type in

            #{item}




		AND a.parentid = #{parentid}



order by rand() limit #{limit}

mapper.update:
UPDATE zquestion SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
