mapper.select:
SELECT 

FROM ztest a

WHERE a.id = #{id}

SELECT 

FROM ztest a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.parentid = #{parentid}


		AND a.parentid in

            #{item}





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ztest a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ztest SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
