mapper.select:
SELECT 

FROM zuser a

WHERE a.id = #{id}

SELECT 

FROM zuser a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.idcode = #{idcode}


		AND a.truename = #{truename}


		AND a.phone = #{phone}


		AND a.create_date = #{createDate}



		AND a.type = #{type}



		AND a.status = #{status}



		AND a.password = #{password}






		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zuser a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zuser SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
