mapper.select:
SELECT 

FROM zuser_test a

WHERE a.id = #{id}

SELECT 

FROM zuser_test a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.userid = #{userid}


		AND a.testid = #{testid}


		AND a.questionid = #{questionid}


		AND a.answerid = #{answerid}


		AND a.isselected = #{isselected}


		AND a.istrue = #{istrue}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC,a.sort

SELECT 

FROM zuser_test a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC,a.sort

mapper.update:
UPDATE zuser_test SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
